    private WeatherForecast[] forecasts;

    private string loadLocation = "";

    private string isCacheData = "";

    private async Task LoadForecast()
    {
        forecasts = null;
        loadLocation = null;
        string recoredKey = "WeatherForecast_" + DateTime.Now.ToString("yyyyMMdd_hhmm");
        forecasts = await cache.GetRecordAsync<WeatherForecast[]>(recoredKey);

        if(forecasts is null)
        {
            forecasts = await ForecastService.GetForecastAsync(DateTime.Now);

            loadLocation = $"Loaded from API at {DateTime.Now}";
            isCacheData = "";

            await cache.SetRecordAsync(recoredKey, forecasts);
        }
        else
        {
            loadLocation = $"Loaded from the cache at {DateTime.Now}";
            isCacheData = "text-danger";
        }
    }

